    import Vue from "vue";
    import {fauth} from '@/firebase';
    import {FirebaseError} from 'firebase';
import User from '../models/User';

    export default Vue.extend({
        name: "LoginPage",
        data() {
            return {
                email: "",
                password: "",
                loading: false,
                error: {
                    email: false,
                    password: false,
                    message: '',
                }
            }
        },

        methods: {
            async login(ev: Event): Promise<any>
            {
                ev.preventDefault();
                this.loading = true;
                this.error = {
                    email: false,
                    password: false,
                    message: '',
                };

                try {
                    const credential = await fauth.signInWithEmailAndPassword(
                        this.email,
                        this.password
                    );
                    const uid = credential.user ? credential.user.uid : '';
                    const user = await (new User).getById(uid);

                    this.$router.replace('/');
                } catch (e) {
                    const error = e as FirebaseError;
                    this.error = {
                        email: error.code == 'auth/user-not-found',
                        password: error.code == 'auth/wrong-password',
                        message: error.message,
                    };

                    // @ts-ignore
                    this.$log.error(error);
                    this.$toasted.error(error.message, {
                        position: 'top-center',
                    });
                }

                this.loading = false;
            }
        },

        mounted() {
            fauth.onAuthStateChanged(user=>{
                if (user) {
                    this.$router.replace('/');
                }
            })
        },

    });
