import "core-js/modules/es6.regexp.replace";
import Vue from 'vue';
export default Vue.extend({
  name: 'ModalWrapper',
  // props: ['class', 'style'],
  data: function data() {
    return {
      showModal: false
    };
  },
  computed: {
    this_class: function this_class() {
      return this.$attrs.class;
    },
    this_style: function this_style() {
      return this.$attrs.style;
    }
  },
  watch: {
    '$route': function $route(route) {
      if (route.meta.showModal) this.$modal.show('modal');
    }
  },
  methods: {
    closeModal: function closeModal(isShow) {
      if (isShow) this.$modal.hide("modal"); // const returnPath = this.$route.matched[0].meta.returnPath || "/";
      // this.$router.replace(returnPath);

      this.$router.replace(localStorage.getItem("last-modal-wrapper") || "/");
    }
  },
  mounted: function mounted() {
    // console.log(this.$route.matched);
    if (this.$route.matched.some(function (route) {
      return route.meta.showModal;
    })) {
      this.$modal.show("modal");
    } else {
      localStorage.setItem("last-modal-wrapper", this.$route.fullPath);
    }
  },
  updated: function updated() {
    // console.log(this.$route.matched);
    if (this.$route.matched.some(function (route) {
      return route.meta.showModal;
    })) {
      this.$modal.show("modal");
    } else {
      localStorage.setItem("last-modal-wrapper", this.$route.fullPath);
    }
  }
});
