@import "~styles/_app_color";
#sidebar_settings {
    a.dropdown-item, .dropdown {
        margin: 0 5px;
        width: auto;
        border-radius: 5px;
        display: flex;
        align-items: center;
        padding: 5px 10px;
        position: relative;

        &:hover {
            background-color: $default-btn-color;
        }
    }

    .dropdown-hover {
        > .dropdown-menu {
            left: 100%;
            top: unset;
            bottom: 0;
            .open & {
                top: 0;
                bottom: unset;
            }
        }
        &:hover > .dropdown-menu {
            display: block;
        }
    }
}
