result:
.task {
    &-check {
        width: 40px;
        height: 40px;
        font-size: 1.25em;
        padding: 0;
        border: 3px solid #ccc;
        line-height: 40px;
        text-align: center;
        background-color: white;
        color: var(--secondary);
        border-radius: 50%;
        &:hover {
            border-color: var(--success);
            background-color: var(--success);
            color: white;
        }
    }
}
.task-list {
    &-item {
        border-left: 6px solid #ccc;
        &.currentActive {
            border-left-color: var(--success);
        }
    }
}
