import Vue from "vue";
import {fauth} from '@/firebase';
import {mapGetters} from 'vuex';


export default Vue.extend({
    props: {},
    computed: {
        // mix the getters into computed with object spread operator
        ...mapGetters([
            'currentUser',
            'currentCompany',
            // ...
        ]),
    },
    data() {
        return {
            perkIntro: '',
            def_perks: [
                {icon: 'fa-gift'},
                {icon: 'fa-taxi'},
                {icon: 'fa-gamepad'},
                {icon: 'fa-film'},
                {icon: 'fa-desktop'},
                {icon: 'fa-laptop'},
                {icon: 'fa-money-bill'},
                {icon: 'fa-graduation-cap'},
                {icon: 'fa-futbol'},
                {icon: 'fa-wheelchair'},
                {icon: 'fa-futbol'},
                {icon: 'fa-coffee'},
                {icon: 'fa-medkit'},
                {icon: 'fa-paw'},
                {icon: 'fa-birthday-cake'},
                {icon: 'fa-child'},
                {icon: 'fa-users'},
                {icon: 'fa-user'},
                {icon: 'fa-utensils'},
                {icon: 'fa-chart-bar'},
                {icon: 'fa-ticket'},
                {icon: 'fa-plane'},
                {icon: 'fa-heart'},
                {icon: 'fa-heartbeat'},
                {icon: 'fa-rebel'},
                {icon: 'fa-pied-piper-alt'},
                {icon: 'fa-hand-spock'},
            ]
        }
    },
    methods: {
        updateCompany: function () {
            this.currentCompany.update();
            this.$toasted.show('Saved!')
        },
        addToPerkCollection: function (perk) {
            if (this.perkIntro == '') this.$toasted.error('Enter description for this icon in textbox above !!!');
            else {
                this.currentCompany.perks.push({
                    icon: perk.icon,
                    name: this.perkIntro
                });
                this.perkIntro = '';
            }
        },
        removeToPerkCollection: function (index) {
            this.currentCompany.perks.splice(index, 1);
        }
    },
});
