result:
#work-experience li {
    margin-bottom: 30px;
}

#scorecard {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    border: 2px solid #edeff0;
    background: #76d691;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 24px;
    color: #ffffff;
}

.vertical-line {
    height: 60px;
    border-left: 1px solid #e4e4e4;
    margin-left: 10px;
    margin-right: 10px;
}

.time-line {
    height: 80px;
    border-left: 3px solid #eee;
    margin-left: 10px;
    margin-right: 10px;
}

.time-line .end-point {
    position: absolute;
    bottom: 0;
    left: 22px;
    width: 10px;
    height: 10px;
    background: #eee;
    border-radius: 10px;
}

.avatar-sm {
    width: 25px;
    height: 25px;
    border-radius: 50%;
    border: 1px solid #e4e4e4;
}

.summary {
    border-left: 3px solid #dee5ee;
}

.summary p {
    font-size: 13px;
}

.card .tabs.row.no-gutters {
    height: 100%;
}

.col-auto {
    overflow-y: auto;
    overflow-x: hidden;
    height: 100%;
    width: 180px;
}

.col-auto ul {
    display: block;
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
}

.col-auto ul li {
    font-size: 12px;
    line-height: 20px;
    font-weight: 200;
    display: block;
    cursor: pointer;
    padding: 0px 5px;
    color: #8a9ba8;
    white-space: nowrap;
}

.col-auto ul li i {
    width: 26px;
    height: 26px;
    border-radius: 100%;
    border: 2px solid #8a9ba8;
    padding: 5px;
    margin-right: 10px;
    font-size: 12px;
    text-align: center;
    display: inline-block;
    font-weight: 900px;
}

.col-auto ul li i:hover {
    background-color: #76d691;
    color: #ffffff;
}

.tab-content.col {
    color: #5e676e;
    overflow-y: auto;
    height: 100%;
}

.tab-content.col .container {
    overflow-y: auto;
}

.active-link {
    color: #5ac0e5;
}

h1 {
    font-size: 24px;
    padding-bottom: 3px;
}

h2 {
    font-size: 20px;
    padding-bottom: 3px;
}

h3 {
    font-size: 16px;
    padding-bottom: 3px;
}

h4 {
    font-size: 13px;
    padding-bottom: 3px;
}

h1 i {
    font-size: 18px;
}

.content {
    width: 100%;
}

.info-candidate .head {
    color: #bbb;
}

.info-candidate .head:hover {
    text-decoration: none;
}

.modal-dialog {
    width: 840px;
    height: 550px;
    margin: 0px auto;
    max-width: 100%;
    background-color: transparent;
}

.modal-content {
    height: 100%;
    margin: 0 auto;
    padding: 0 0px;
    color: white;
    background-color: transparent;
    border: 0;
}

.my-modal-body {
    padding: 20px;
    background-color: #384047;
}

.my-modal-footer {
    display: flex;
    flex: 1;
    position: relative;
    height: 100%;
    overflow: hidden;
    background-color: #fafafa;
}

.avatar-candidate {
    height: 80px;
    width: 80px;
    background-color: #e32256;
    border-radius: 100%;
    border: 2px solid white;
    display: inline-block;
    vertical-align: top;
    margin-right: 10px;
}

.avatar-candidate::before {
    content: "A";
    font-size: 42px;
    position: absolute;
    top: 0;
    left: 0;
    width: 80px;
    height: 80px;
    line-height: 76px;
    text-align: center;
}

.avatar-candidate::after {
    content: "2";
    background-color: #76d691;
    border: 2px solid #edeff0;
    border-radius: 100%;
    text-align: center;
    position: absolute;
    top: 56px;
    left: 50px;
    padding: 6px 0;
    line-height: 16px;
    width: 29px;
    height: 29px;
    display: inline-block;
}

.avatar-candidate-sm {
    height: 50px;
    width: 50px;
    background-color: #e32256;
    border-radius: 100%;
    border: 2px solid white;
    display: flex;
    align-items: center;
    margin-right: 10px;
}

.avatar-candidate-sm::before {
    content: "A";
    font-size: 21px;
    top: 0;
    left: 0;
    width: 75px;
    height: 75px;
    line-height: 76px;
    text-align: center;
    color: white;
}

.info-candidate {
    display: inline-block;
    padding: 0%;
    margin: 0%;
}

.info-candidate ul {
    list-style-type: none;
    padding: 0%;
    margin: 0px;
}

.info-candidate ul li {
    font-size: 12px;
    display: inline;
    margin-right: 5px;
    padding: 0%;
    color: #bbb;
}

.body {
    font-weight: bold;
}

.action-candidate {
    padding: 0%;
    margin: 0%;
    text-align: right;
}

.action-candidate ul {
    list-style-type: none;
    padding: 0%;
    margin: 0px;
    margin-bottom: 5px;
}

.action-candidate ul li {
    font-size: 12px;
    display: inline;
    margin-left: 5px;
    padding: 6px 12px;
    color: white;
    background-color: #5ac0e5;
    border: 0;
    border-radius: 3px;
    line-height: 1.5;
}

.action-candidate ul li i {
    font-size: 11px;
    line-height: 1;
    display: inline-block;
    padding: 0;
    margin: 0;
}

ul.action-candidate-third li {
    background-color: #76d691;
}

ul.action-candidate-second li {
    background-color: #edeff0;
    color: #68747e;
}

.content-candidate {
    margin: 0 0 20px 20px;
    background-color: white;
    position: absolute;
    left: 160px;
    right: 20px;
    top: 20px;
    border: 1px solid #edeff0;
    padding: 20px;
    border-radius: 4px;
    height: calc(100% - 40px);
    overflow: auto !important;
}

@import "~@fullcalendar/core/main.css";
@import "~@fullcalendar/daygrid/main.css";
@import "~@fullcalendar/timegrid/main.css";
