import Vue from 'vue';
import SidebarSettings from './SidebarSettings.vue';
import SidebarNotification from './SidebarNotification.vue';
import {mapGetters, mapMutations} from "vuex";

export default Vue.extend({
    name: "Sidebar",

    components: {
        SidebarSettings,
        SidebarNotification,
    },

    data() {
        return {
            nav: {
                open: true,
            },
            positions: [ // test
                {
                    status: 'public',
                    id: 'asd',
                    name: "Senior DevOps",
                },
                {
                    status: 'draft',
                    id: 'dsa',
                    name: "Junior Tester"
                },
                {
                    status: 'archived',
                    id: '123456',
                    name: "Fullstack Engineer",
                }
            ]
        }
    },
    computed: {
        // mix the getters into computed with object spread operator
        ...mapGetters([
            'currentUser',
            'currentCompany',
            // ...
        ]),

        classSidebar(): any {
            return {
                "text-light": true,
                "text-left": true,
                "open": this.nav.open,
                "app-sidebar": true,
            }
        }
    },
    methods: {
        ...mapMutations([
            'modals/showAddTask',
            'modals/showAddCandidate',
        ]),

        showAddTaskModal(event): void {
            event.preventDefault();
            this['modals/showAddTask'](true);
        },

        showAddCandidateModal(event): void {
            event.preventDefault();// chan trigger cua nav-link cua menu
            this['modals/showAddCandidate'](true);
        },

        showNewPositionModal(event): void {
            event.preventDefault();
            this.$router.push({name: 'NewPosition'});
        }
    },
})
